this.initTab=function(name){jQuery(name).tabs();jQuery(name).attr("style","display:run-in");}
function getTabSelected(name){var selected=-1;jQuery(document).ready(function(){selected=jQuery(name).tabs("option","selected");});return selected;}
function setTabSelected(name,tab){jQuery(document).ready(function(){jQuery(name).tabs("option","selected",tab);});}
(function($){$.fn.disableTab=function(tabIndex,hide){var disabledTabs=this.tabs("option","disabled");if($.isArray(disabledTabs)){var pos=$.inArray(tabIndex,disabledTabs);if(pos<0){disabledTabs.push(tabIndex);}}
else{disabledTabs=[tabIndex];}
this.tabs("option","disabled",disabledTabs);if(hide===true){jQuery(this).find('li:eq('+tabIndex+')').css("display","none");}
return this;};$.fn.enableTab=function(tabIndex){$(this).find('li:eq('+tabIndex+')').css("display","block");this.tabs("enable",tabIndex);return this;};})(jQuery);this.hideTab=function(name,id){jQuery(name).disableTab(id,true);}
this.showTab=function(name,id){jQuery(name).enableTab(id);}